--
-- Table structure for table `score`
--

DROP TABLE IF EXISTS `score`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `score` (
  `ID` int(11) NOT NULL AUTO_INCREMENT COMMENT '内码',
  `SCORE_YEAR` int(11) DEFAULT NULL COMMENT '学年',
  `SEMESTER` varchar(10) DEFAULT NULL COMMENT '学期',
  `SCORE_GRADE` varchar(10) DEFAULT NULL COMMENT '年级',
  `SCORE_CLASS` varchar(11) DEFAULT NULL COMMENT '班级',
  `SCORE_NO` varchar(50) DEFAULT NULL COMMENT '学号',
  `STU_NAME` varchar(20) DEFAULT NULL COMMENT '姓名',
  `STU_SEX` varchar(10) DEFAULT NULL COMMENT '性别',
  `AAS` varchar(10) DEFAULT NULL COMMENT '文理科,1文科2理科',
  `KEMU` varchar(50) DEFAULT NULL COMMENT '考试名称',
  `CHINESE` varchar(10) DEFAULT NULL COMMENT '语文',
  `KEMATCH` varchar(10) DEFAULT NULL COMMENT '数学',
  `ENGLISH` varchar(10) DEFAULT NULL COMMENT '英语',
  `PHYSICAL` varchar(10) DEFAULT NULL COMMENT '物理',
  `CHEMISTRY` varchar(10) DEFAULT NULL COMMENT '化学',
  `BIOLOGICAL` varchar(10) DEFAULT NULL COMMENT '生物',
  `HISTORY` varchar(10) DEFAULT NULL COMMENT '历史',
  `POLITY` varchar(10) DEFAULT NULL COMMENT '政治',
  `GEOGRAPHY` varchar(10) DEFAULT NULL COMMENT '地理',
  `TOTAL_SCORE` varchar(10) DEFAULT NULL COMMENT '总分',
  PRIMARY KEY (`ID`)
