INSERT INTO `show_img` VALUES (3,'2015高考喜报','1','/file/imgFile/20151124111854640.jpg','','1'),(10,'1984届毕业30周年同学会','1','/file/imgFile/20150324091948487.jpg','','2'),(11,'八建助学','2','/file/imgFile/20150324091836143.jpg','','2'),(12,'校园风景','3','/file/imgFile/20150320154421432.jpg','','2'),(13,'校园风景','','/file/imgFile/20150320154740495.jpg','','2'),(14,'湖北省武汉市新洲区第一高级中学(新洲一中)是一所座落于新洲区邾城街的三年制普通高中，属湖北省首批办好的重点中学和示范性高中，是武汉市对外开放学校。学校以办学的高水平、高品位、高质量和雄厚的综合实力享誉江城，蜚声荆楚。学校以突出的办学实绩，赢得了湖北省文明单位、省市示范性高中、湖北省教科研50强、武汉市对开放学校等17项省级和15项市级殊荣。经过十多年高中教育的发展，学校已经在武汉市普通高中率先实现了教学现代化、办公网络化、管理科学化、校园生态化。','','/file/imgFile/2014110611113351.jpg','http://zhaoyou.jsp.fjjsp01.com/jsp/newDetail.jsp?ID=69','3'),(15,'湖北省武汉市新洲区第一高级中学(新洲一中)是一所座落于新洲区邾城街的三年制普通高中，属湖北省首批办好的重点中学和示范性高中，是武汉市对外开放学校。学校以办学的高水平、高品位、高质量和雄厚的综合实力享誉江城，蜚声荆楚。学校以突出的办学实绩，赢得了湖北省文明单位、省市示范性高中、湖北省教科研50强、武汉市对开放学校等17项省级和15项市级殊荣。经过十多年高中教育的发展，学校已经在武汉市普通高中率先实现了教学现代化、办公网络化、管理科学化、校园生态化。','','/file/imgFile/20141018013822591.jpg','http://zhaoyou.jsp.fjjsp01.com/jsp/newDetail.jsp?ID=20','4'),(16,'校园风景','1','/file/imgFile/20150331100008834.jpg','http://zhaoyou.jsp.fjjsp01.com/jsp/newDetail.jsp?ID=169','5'),(18,'校园风景','3','/file/imgFile/20150331092712178.jpg','http://zhaoyou.jsp.fjjsp01.com/jsp/newDetail.jsp?ID=168','5'),(19,'秋季田径运动会','4','/file/imgFile/20150331102206397.jpg','http://zhaoyou.jsp.fjjsp01.com/jsp/newDetail.jsp?ID=170','5'),(20,'新洲一中1984届毕业30周年同学会盛大举行','2','/file/imgFile/2015031810323542.jpg','http://zhaoyou.jsp.fjjsp01.com/jsp/newDetail.jsp?ID=161','1'),(21,'我校2015高考再创辉煌','','/file/imgFile/20151124105732640.jpg','','5');
